// Reduce the largest width
function calculateTabOverflow(base) {
    var currentTop = 0;
    var overflowTop = 0;
    var dsCount = 0;
    var arrTabRef = [];
    var arrTabWidth = [];
    logGroup('calculateTabOverflow(' + base + ')');
    if (!isMobileWidth()) {
        $('.mobile-list a', base).each(function () {
            $(this).width('auto');
            arrTabWidth[dsCount] = Math.ceil($(this).width()) + 1;
            arrTabRef.push(this);
            if (($(this).offset().top != currentTop) && (dsCount != 0)) {
                log('Tab dropped');
                reduceLargestWidth(arrTabRef, arrTabWidth, parseInt(currentTop), this);
            }
            currentTop = $(this).offset().top;
            dsCount++;
        });
    }
    else { $('.mobile-list a', base).each(function () { $(this).removeAttr('style'); }); }
    log('Mobile view:', isMobileWidth());
    logGroupEnd();
}

function reduceLargestWidth(arrRef, arr,target,ele) {
    var largest = 0;
    var largestIndex = 0;
    var count = 0;
    while (Math.floor($(ele).offset().top) != target) {
        for (i = 0; i < arr.length; i++) {
            largestIndex = (arr[i] > largest) ? i : largestIndex;
            largest = arr[largestIndex];
        }
        arr[largestIndex] -= 1;
        for (i = 0; i < arr.length; i++) {
            $(arrRef[i]).width(arr[i]);
        }
        //log(target, parseInt($(ele).offset().top));
        count++;
        if($(arrRef[largestIndex]).find(".tab-text").length) { 
        	var strLargest =  $(arrRef[largestIndex]).find(".tab-text").html().trim();
        	var pos = strLargest.lastIndexOf(' ');
        	if(pos > 0) {
	        	strLargest = strLargest.substring(0, pos) + '<br/>' + strLargest.substring(pos + 1);
	        	$(arrRef[largestIndex]).find(".tab-text").html(strLargest);
	        	$(arrRef[largestIndex]).width('auto');
	        	arr[largestIndex] = $(arrRef[largestIndex]).width();
        	}
        }
        if (count > 1000) break; //prevent endless loop
    }
    log('Largest width:', largest);
}


