/*
    ref. height, outerHeight(true)
    http://stackoverflow.com/a/17845094/248616
*/
function nypSetSameHeight(elemsToSet) {
    if (elemsToSet) {
        //get max height
        var maxHeight = -1 * Infinity;
        checkMax(0, elemsToSet, function() {
            setHeight(maxHeight, elemsToSet)
        });
        
        function checkMax(i, elemsToSet, onDone) {
            //check if ending
            var n = $(elemsToSet).length;
            if (i >= n) {
                if (onDone) {
                    onDone();
                }
                return;
            }
            
            //check with max
            var elemToSet = $(elemsToSet).eq(i);
            var h = $(elemToSet).outerHeight();

            if (h > maxHeight) {
                maxHeight = h;
            }
            checkMax(i+1, elemsToSet, onDone);
        }
        
        function setHeight(maxHeight, elemsToSet) {
            //set all heights as max one
            $(elemsToSet).each(function(){
                $(this).outerHeight(maxHeight);
            });
        }
    
    }
}

function eraseCookie(cname) {
    document.cookie = cname + '=; expires=Thu, 01-Jan-1970 00:00:01 GMT; path=/';
}

function getCookie(cname) {
    var name = cname + "=";
    var ca = document.cookie.split(';');
    for (var i = 0; i < ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') {
            c = c.substring(1);
        }
        if (c.indexOf(name) == 0) {
            return c.substring(name.length, c.length);
        }
    }
    return "";
}

function setCookie(cname, cvalue, extimes) {
    var d = new Date();
    d.setTime(d.getTime() + extimes);
    var expires = "expires=" + d.toUTCString();
    document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
}
