jQuery.validator.addMethod("requiredRecapcha", function(value, element) {
    return $('.g-recaptcha-response').val() == 0 ? false : true;
}, "Please verify that you are not a robot.");

$(document).ready(function() {
	if($('#myrecaptcha').length <= 0) {
		return;
	}	
	if (/MSIE (\d+\.\d+);/.test(navigator.userAgent) || navigator.userAgent.indexOf("Trident/") > -1 ){
	    recaptchaReady();
	}
  
     $("#send_enq").unbind().click(function () {
        $('#enq-form').submit();
     });
    $('input, textarea').placeholder();
    
    //form validation
    $('#enq-form').validate({
        ignore: [],
        errorClass: 'invalid',
        rules: {
            enq_name: { 
                required: true
            },
            enq_email: { 
                required: true,
                email: true 
            },
            enq_mobile: { 
                required: true,
                digits: true, 
                minlength: 8, 
                maxlength: 12 
            },
            enq_enquiry: { 
                required: true
            },
            cap_code: { 
                required: true
            },
            g_recaptcha: {
                requiredRecapcha: true
            }
        },
        messages: {
            enq_name: "Please enter your name",
            enq_email: { required: "Please enter a valid e-mail address",
                         email: "Please enter a valid e-mail address"
                        },
            enq_mobile: { required: "Please enter your mobile number",
                          digits: "Please use numeric characters only"
                        },
            enq_enquiry: "Please enter your enquiry",
            cap_code: "Please enter the security code",
            g_recaptcha: "Please verify that you are not a robot"
        },
        errorElement: "span",
        errorPlacement: function(error, element) {
            error.addClass('help-block');
            error.insertAfter(element)
        },
        submitHandler: function(form) {

            var formData = $(form).serialize();
            $('#send_enq').addClass('blink-loader');
            jQuery.ajax({
                        cache: false,
                        type: 'POST',
                        url: enquiryFormAction,
                        data: formData,
                        success: function(rspns) {
                        	var rcode = rspns.status;
                            if (rcode == '1') {
                            	$('#enq-cover').fadeIn();
                            } else {
                            	alert(rspns.info);
                            }
                            $('#send_enq').removeClass('blink-loader');
                        },
                        error: function() { 
                        	console.error('[ERROR] Something went wrong. Please try again later.'); 
                        },
                        statusCode:{404: function() { 
                        	console.error('[Error - 404] The requested file not found. Sorry for this inconvenience. Please contact the system administrator.'); 
                        }},
                        statusCode:{500: function() { 
                        	console.warn('[WARNING - 500] Something went wrong with the server and it could not complete your request. You could try again later, but if the problem persists, please contact your technical support.'); 
                        }}
                    });
            return false;
        }
    });
    
    // $('#cap-refresh').click(generateCaptcha);
    
    $('#rsp-close').click(function() {
        $('#enq-cover').fadeOut();
        $('#enq-form')[0].reset();
        // generateCaptcha();
    });
    
});
$(document).ready(function () {
	setTimeout(function () {
	$('.ds-image-text img').each(function () {
	    $(this).attr('data-original', $(this).attr('src')).addClass('lazy').removeAttr('src');
	});
	}, 500);
	setTimeout(function () {
	    $('img.lazy').lazyload({
	        effect: "fadeIn"
	    });
	}, 1000);	
});
$(document).ready(function() {
    $("#testimoniallist_selectAll").click(function () {
        if ($(this).children("input").is(':checked')) {
            $(".filter-group-child").each(function () {
                $(this).children("input").prop('checked', true);
            });
        }
        else {
            $(".filter-group-child").each(function () {
                $(this).children("input").prop('checked', false);
            });
        }
        $(".filter-group-child").each(function () {
            if ($(this).children("input").is(':checked')) {
                $(this).removeClass("filter-overlay");
            }
            else {
                $(this).addClass("filter-overlay");
            }
        })
    });

    $('#testimoniallistreference_filter').click(function() {
        var tagIds = [];
        $(".filter-group-child").each(function() {
            var input = $(this).children("input");
            if (input.is(":checked")) {
                tagIds.push(input.val());
            }
        });
        $(".testimonial.row").each(function() {
            var dataTagId = $(this).attr("data-schools");
            var panelMatch = false;
            for (var i = 0; i < tagIds.length; i++) {
                if (dataTagId.indexOf(tagIds[i]) > -1) {
                    panelMatch = true;
                    $(this).show();
                } 
            }
            if (!panelMatch) {
                $(this).hide();
            }
        });
    });
});

