$(document).ready(function () {
    $('.tablinks .ds-horizon-tab .mobile-list li').each(function () {
        var curUrl = window.location.href;
        var tabUrl = $(this).find('a').attr('href');
        if (tabUrl !== 'undefined' && tabUrl !== '' && curUrl.indexOf(tabUrl) !== -1) {
            $(this).addClass('active');

            var btnimgurl = $(this).find('img').attr('src');
            var btncaption = $(this).find('.tab-text').html();
            if (btnimgurl === undefined) {
                $('.tablinks .ds-horizon-tab .mobile-selector li a span.menu-wrapper').html('<span class="tab-text">' + btncaption + '</span>');
            }
            else {
                $('.tablinks .ds-horizon-tab .mobile-selector li a span.menu-wrapper').html('<span class="tab-image"> <img src="' + btnimgurl + '"/></span><span class="tab-text">' + btncaption + '</span>');
            }
        } else {
            $(this).removeClass('active');
        }
    });
});

//Share FB
function shareFB(url) {
    var winHeight = 350;
    var winWidth = 650;
    var winTop = (screen.height / 2) - (winHeight / 2);
    var winLeft = (screen.width / 2) - (winWidth / 2);
	window.open('https://www.facebook.com/sharer/sharer.php?u=' + url, 'sharer', 'top=' + winTop + ',left=' + winLeft + ',toolbar=0,status=0,width=' + winWidth + ',height=' + winHeight);
}
//Share Tweet
function shareTweet(title, url) {
    var winHeight = 350;
    var winWidth = 650;
    var winTop = (screen.height / 2) - (winHeight / 2);
    var winLeft = (screen.width / 2) - (winWidth / 2);
    window.open("https://twitter.com/share?url="+ url +"&text=" + decodeURI(title),'Tweet', 'top=' + winTop + ',left=' + winLeft + ',toolbar=0,status=0,width=' + winWidth + ',height=' + winHeight);
}
