$(document).ready(function () {
    $('ul li.expandable i.glyphicon-plus').click(function (e) {
        $(this).toggleClass("glyphicon-plus glyphicon-minus");
        $(this).parent().children('ul').slideToggle();
        $('.glyphicon-minus', $(this).parent().children('ul')).parent().children('ul').slideToggle();
        $('.glyphicon-minus', $(this).parent().children('ul')).toggleClass("glyphicon-plus glyphicon-minus");
        e.stopPropagation();
        return false;
    });
    $(".collapseall").click(function () {
        $('.parentul .glyphicon-minus').parent().children('ul').slideToggle();
        $('.parentul .glyphicon-minus').toggleClass("glyphicon-plus glyphicon-minus");
    });
    $(".expandall").click(function () {
        $('.parentul .glyphicon-plus').parent().children('ul').slideToggle();
        $('.parentul .glyphicon-plus').toggleClass("glyphicon-plus glyphicon-minus");
    });
});
