function nypToggleAccordionRow(row, doExpand)
{

    var header = $(row).find('*[data-parent]').eq(0);
    var content = $(row).find('.accordionPanelContent').eq(0);

    var ariaExpanded = (doExpand)? 'true':'false';
    var collapsed = 'collapsed';
    var inContent = 'in';

    $(header).attr('aria-expanded', ariaExpanded);
    if (doExpand) {
        $(header).removeClass(collapsed);
        $(content).addClass(inContent);
    } else {
        $(header).addClass(collapsed);
        $(content).removeClass(inContent);
    }
}
