$(function(){
    //When a row is clicked, 
    //do scroll down to get rid of `sticky menu overlapping`
    $(".micrositeAccordionPanelHeader").click(function() {
        //do scroll if required by `activeScroll` in `input data` only
        var accordionRoot = $(this).closest('.nyp-panel-group');
        var activeScroll = accordionRoot.hasClass('activeScroll');
        if (!activeScroll) {
            return;
        }

        //get row id
        var rowID = $(this).attr('id');
        
        //Wait until the expanding finishes
        $('.nyp-panel-group').on('shown.bs.collapse', function() { //ref. http://stackoverflow.com/a/31590144/248616
            //Find the row that is clicked as above
            var accordionTable = $(this);
            var row = accordionTable.find('#'+rowID).eq(0);
            
            if (row) {
                var scrollTo = $(row);
                var scrollTime = 400;//millisecond
                var stickyMenuHeight = $('#nypHeader').height();
                var extra = 20;
                
                var pixel2Scroll = 0
                    + $(scrollTo).offset().top //scroll to the clicked rows
                    - stickyMenuHeight //scroll more to go below the `sticky top menu`
                    - extra //more extra scroll down i.e. top padding vs `sticky menu`
                ;

                //do scroll ref. http://stackoverflow.com/a/6677069/248616
                //$('html, body').animate( 
                //    {scrollTop: pixel2Scroll}, scrollTime
                //);
            }

            //unregister this handler => not stack up as `continous row click` ref. http://stackoverflow.com/a/6677069/248616
            $(this).off('shown.bs.collapse'); 
        });

    });
});
