$(document).ready(function() {
		//$("#block-a" + ".block-info").css("display": "block");
		var startX, startY, tap;
		var screenWidth = $(window).width();
		if (screenWidth <= 768) {
			$('#imageFullScreen').smartZoom({
				'maxScale' : 5
			});
		}

		function getCoord(e, c) {
			return /touch/.test(e.type) ? (e.originalEvent || e).changedTouches[0]['page'
					+ c]
					: e['page' + c];
		}

		function setTap() {
			tap = true;
			setTimeout(function() {
				tap = false;
			}, 500);
		}

		$(window).on("resize", function() {
			$('#imageFullScreen').smartZoom("destroy");
			$(".mobile-zoom svg").removeAttr("style");
			screenWidth = $(window).width();
			if (screenWidth <= 768) {
				$('#imageFullScreen').smartZoom({
					'maxScale' : 5
				});
			}
		});
		$(document)
				.on('touchstart', 'g.group-block', function(ev) {
					startX = getCoord(ev, 'X');
					startY = getCoord(ev, 'Y');

				})
				.on(
						'touchend',
						'g.group-block',
						function(ev) {
							// If movement is less than 20px,
							// execute the handler
							if (Math
									.abs(getCoord(ev, 'X') - startX) < 20
									&& Math.abs(getCoord(ev, 'Y')
											- startY) < 20) {
								showBlockInfor($(this));
								ev.preventDefault();
							}
							setTap();
						}).on('click', 'g.group-block',
						function(ev) {
							if (!tap) {
								showBlockInfor($(this));
							}
							ev.preventDefault();
						});

		function showBlockInfor(block) {
			var id = block.attr("id");
			$(".block-info").removeClass("active");
			$(".block-info").css("display", "none");
			$("#" + id + ".block-info").addClass("active");
			$("#" + id + ".block-info").css("display", "block");
		}
		;

	});
